<?php

eval(base64_decode(gzuncompress(base64_decode(''))));

class tiaop_acs_sml {
	// Constants
	const TIAOP_ACSSTO = 30;
	const TIAOP_PLUGIN = "Transaction Integration for AffiliateWP and OptimizePress";

	// Check for valid license
	public function check_license($license_key) {
		// Set params
		$api_params = array(
			"slm_action" => "slm_check",
			"secret_key" => TIAOP_ACSVLK,
			"license_key" => $license_key
		);

		// Set options
		$api_options = array(
			"timeout" => tiaop_acs_sml::TIAOP_ACSSTO,
			"sslverify" => false
		);

		// Check the server
		$results = wp_remote_get(add_query_arg($api_params, TIAOP_ACSLSL), $api_options);

		// Check for error in the results
		if (is_wp_error($results))
			return false;

		// Get license data
		$results = json_decode(wp_remote_retrieve_body($results), true);

		if($results["result"] == "success" && $results["status"] == "active")
			return true;

		return false;
	}

	public function update_license($license_key, $activate) {
		// Set params
		$api_params = array(
			"slm_action" => $activate ? "slm_activate" : "slm_deactivate",
			"secret_key" => TIAOP_ACSVLK,
			"license_key" => $license_key,
			"registered_domain" => $_SERVER["SERVER_NAME"],
			"item_reference" => urlencode(tiaop_acs_sml::TIAOP_PLUGIN),
		);

		// Set options
		$api_options = array(
			"timeout" => tiaop_acs_sml::TIAOP_ACSSTO,
			"sslverify" => false
		);

		// Check the server
		$results = wp_remote_get(add_query_arg($api_params, TIAOP_ACSLSL), $api_options);

		// Check for error in the results
		if (is_wp_error($results))
			return false;

		// Get license data
		$results = json_decode(wp_remote_retrieve_body($results), true);

		if($results["result"] == "success") {
			//Save the license key
			settings_fields("tiaop-license");
			update_option("tiaop_license_key", $activate ? $license_key : "");
			return true;
		}
		else {
			echo "<span style='color:red'>" . $results["message"] . "</span>";
		}

		return false;
	}
}
?>